import React from "react";
import "./App.css";

class StandardPlay extends React.Component {
  handleClick = (e) => {
    clicked = e.target.id;
    /* send post request with monty type and door clicked then remove that door from door_items*/
    /* update state with a callback */
  }

  render () {
    const door_items = []
    var i;
    for (i=0; i<this.props.doors; i++){
      door_items.push(<button id="{i}" onClick="handleClick"> Door {i} </button>)
    }
    return (
      <div classNmae="main">
        <h1> {this.props.monty} </h1>
        <p> Choose a door! </p>
        <div>
        {door_items}
        </div>
      </div>
    )
  }

}

export default StandardPlay;
